// plugin.js
tinymce.PluginManager.add("anchor",function(b){function a(){b.windowManager.open({title:"Anchor",body:{type:"textbox",name:"name",size:40,label:"Name",value:b.selection.getNode().id},onsubmit:function(c){b.execCommand("mceInsertContent",false,b.dom.createHTML("a",{id:c.data.name}))}})}b.addButton("anchor",{icon:"anchor",tooltip:"Anchor",onclick:a,stateSelector:"a:not([href])"});b.addMenuItem("anchor",{icon:"anchor",text:"Anchor",context:"insert",onclick:a})});